  //   }
    // var code_challenger = base64URLEncode(sha256(code_verifier));
  var code_challenger = base64URLEncode(Base64.stringify(sha256(code_verifier)));
  console.log("code_verifier");
  console.log(code_verifier);
  console.log("code_challenger");
  console.log(code_challenger);
  const SigninHandler=()=>{
    console.log("in handler")
    console.log(code_challenger)
    // ur=`https://dev-12u2388w.us.auth0.com/authorize?response_type=code&code_challenge=${code_challenger}&code_challenge_method=S256&client_id=UeOlj5xtXsESZ2PGctxyOPoz22ybghi3&redirect_uri=http://localhost:3000/&scope=openid%20profile&state=xyzABC123`
