        { width: 1, itemsToShow: 1 },
        { width: 400, itemsToShow: 2 },
        { width:600, itemsToShow:3},
        { width: 800, itemsToShow: 4 },
        { width: 1000, itemsToShow: 5 },
        { width: 1200, itemsToShow: 6 },
    ];
    return(
    <Carousel breakPoints={breakPoints}>
        {Images.map(image =>
            <ImageCard onClickWatchList={onPressWatchList} 
            key={image.id} id={image.id} url={image.url} link={image.link} title={image.title} description={image.description} subtitle={image.subtitle} duration={image.duration} ></ImageCard>
        )}
        <p>hiii</p>
