import React from "react";
import "./WatchlistCard.css";
const WatchlistCard = (props) => {
  // const clickhandler=()=>{
  //     console.log(props.name)
  // }
  const onRemoveWatchList = () => {
    var ab = "c_" + props.id;
    var cd = Number(localStorage.getItem(ab));
    cd = cd + 1;
    localStorage.setItem(ab, String(cd));
    console.log("in imagecard" + props.id);
    props.onRemoveWatchList(props.id);
  };

  return (
    <div className="watchlist_imagecard-oute">
      <div className="watchlist_imagecard-inner">
        <a href={props.link}>
          <img className="ima" src={props.url} alt=""></img>
        </a>
        <div className="watchlist_hide">
          <div className="watchlist__title">{props.title}</div>
          <div className="watchlist__duration">{props.duration}</div>
          <div className="watchlist__subtitle">{props.subtitle}</div>
          <div className="watchlist__description">{props.description}</div>
          <div className="watchlist__watchmovie">
            <img
              src="data:image/svg+xml;base64,"
              alt=""
            />
            <button>WATCH MOVIE</button>
          </div>
          <div className="watchlist__addtowatchlist">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Crystal_button_cancel.svg/1024px-Crystal_button_cancel.svg.png"
              alt=""
            />
            <button onClick={onRemoveWatchList} style={{ color: "red" }}>
              REMOVE FROM WATCH LIST
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WatchlistCard;
